mapper.select:
select loc_num, hall_code
from locker_record
where stu_id = #{stuId}
and hall_code = #{hallCode}

mapper.update:
update locker
set loc_status = 'Y'
where loc_num = #{locNum}
and hall_code = #{hallCode}

update locker
set loc_status = 'N'
where loc_num = #{locNum}
and hall_code = #{hallCode}
