mapper.select:
select study_title
from study
where study_code = #{studyCode}

select stu_id, sm_leader, study_code
from study_member
where study_code =#{studyCode}
and stu_id =#{stuId}

mapper.insert:
insert into study_member(
	study_code, stu_id, study_status
)values(
	#{studyCode}, #{stuId}, #{studyStatus}
)
